import React from 'react';
import "./AddAuthorForm.css";
// and the webpack setup in createReactApp will pick up that CSS import, do some magic, and make it all work


class AuthorForm extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            name : '',
            imageUrl: ''
        }
    }

    onFieldChange(event){
        this.setState({
           [event.target.name]: event.target.value
        });
    }

    render(){
     return 
        <form>
               <div className="AddAuthorForm__input">
                   <label htmlFor="name">Name</label>
                   <input type="text" name="name" value={this.state.name}/>
               </div>
               <div className="AddAuthorForm__input">
                   <label htmlFor="imageUrl">Image URL</label>
                   <input type="text" name="imageUrl" name={this.state.imageUrl}/>
               </div>
           </form>;
    }
}


// for is reserved js word, so htmlFor is used in jsx
function AddAuthorForm({match}){
    return <div className="AddAuthorForm">
           <h1>Add Author</h1>
           <AuthorForm />
        </div>;
}

export default AddAuthorForm;
